class Document:    def __init__(self, docId, playId, sceneId, sceneNum, text, length):        self.docId = docId        self.playId = playId        self.sceneId = sceneId        self.sceneNum = sceneNum        self.text = text        self.length = length        self.score = 0    def getId(self):        return self.docId    def getSceneId(self):        return self.sceneId    def getPlayId(self):        return self.playId    def __repr__(self):        return f"{self.score}:{self.docId} : {self.sceneId}\n"